xf:var.value:
$model/fb:submission[
    ends-with(@id, '-submission') and not(
        @id = (
            ../fb:action[
                ends-with(@id, '-binding') and
                @id != concat($instance/initial-name, '-binding')
            ]/xf:action[1]/xf:send/@submission
        )
    )
]

fr:dnd-repeat.ref:
let $actions :=
    xf:action[
        @class = (
            'fr-set-control-value-action',
            'fr-itemset-action',
            'fr-save-to-dataset-action'
        )
    ]
return
    if (exists($actions)) then
        $actions
    else
        instance('fb-binding-action-template')/xf:action[@class =
            (
                if ($event-not-before-controls-ready)
                then 'fr-set-control-value-action'
                else 'fr-save-to-dataset-action'
            )
        ]
