xf:insert.origin:
xf:element(
    'event',
    (
        xf:attribute('type', event('xxf:type')),
        xf:attribute('target', event('xxf:targetid')),
        for $c in event('error-type')
        return xf:attribute('error-type', $c)
    )
)

xf:element(
    'event',
    (
        xf:attribute('type', event('xxf:type')),
        xf:attribute('target', event('xxf:targetid')),
        for $c in event('error-type')
        return xf:attribute('error-type', $c)
    )
)

xf:element(
    'event',
    (
        xf:attribute('type', event('xxf:type')),
        xf:attribute('target', event('xxf:targetid')),
        for $c in event('error-type')
        return xf:attribute('error-type', $c),
        for $i in 1 to 4,
            $p in concat('p', $i),
            $v in event($p)
        return
            xf:attribute($p, $v)
    )
)
